import React, { useState, FormEvent } from "react";

import { Container, Content } from "./styles";

import api from "../../services/api";
import { createPortal } from "react-dom";

interface CepProps {
  status: string;
  code: string;
  state: string;
  city: string;
  district: string;
  address: string;
}

const Correios: React.FC = () => {
  const [inputcep, SetInputCep] = useState("");

  const [cep, SetCep] = useState<CepProps[]>([]);

  async function handlePesqCep(
    event: FormEvent<HTMLFormElement>
  ): Promise<void> {
    event.preventDefault();
    const response = await api.get<CepProps>(`/${inputcep}.json`);
    const find = response.data;
    console.log(find);
    SetCep([...cep, find]);
  }
  return (
    <Container>
      <Content>
        <h1>Busca de CEP Api Correios</h1>
        <form onSubmit={handlePesqCep}>
          <input
            name="cep"
            value={inputcep}
            onChange={(e) => SetInputCep(e.target.value)}
            placeholder="Digite seu cep"
          />

          <button type="submit">Pesquisar</button>
        </form>

        <div>
          {cep.map((results) => (
            <div key={results.code}>
              <div>
                <strong>{results.city}</strong>
                <p>{results.address}</p>
              </div>
            </div>
          ))}
        </div>
      </Content>
    </Container>
  );
};

export default Correios;
