import styled from "styled-components";

export const Container = styled.div`
  display: flex;
  align-items: center;
`;

export const Content = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  h1 {
      align-content: center;
      margin-top:820px
  }

  form {
    width: 340px;
    color: #a8a8;

    input {
      flex: 1;
      color:#a1a
      align-content: center;
      margin-right: 10px;
      flex-direction: column;
    }
  }
`;
